  // For Sorting Price
  sort(event: any) {
    switch(event.target.value) {
      case "low":
        {
          this.items = this.items.sort(((low:any,high:any)=> low.price - high.price));
          break;
        }
      case "high":
        {
          this.items = this.items.sort(((low:any,high:any)=> high.price - low.price));
          break;
        }
      default: 
        {
          this.items = this.items.sort(((low:any,high:any)=> low.price - high.price));
          break;
        }
    }
    return this.items;

  }
  



  // For Filter Color
  filter(event: any) {
    switch(event.target.value) {
      case "red":
        {
          this.items = this.items.filter((items)=> items.color === 'red');
          break;
        }
      default: 
        {
          this.items = this.items.filter((items)=> items.color === 'red');
          break;
        }
    }
    return this.items;
  }


  

  addCart(category:any) {
        // this.router.navigateByUrl('post/cartlist');
        // console.log(category);
        let cartData = localStorage.getItem('localcart');
        if (cartData == null) {
          let storeDataGet:any = [];
          storeDataGet.push(category);
        localStorage.setItem('localcart', JSON.stringify(storeDataGet))
        }
        else {
          var id = category.productId;
          let index:number = -1;
          this.itemsCart = JSON.parse(localStorage.getItem('localcart') || '{}');
          for(let i=0; i<this.itemsCart.length;i++){
            if(parseInt(id) === parseInt(this.itemsCart[i].productId)){
              this.itemsCart[i].quantity = category.quantity;
              index = i;
              break;
            }
          }
          if(index == -1){
            this.itemsCart.push(category);
            localStorage.setItem('localcart', JSON.stringify(this.itemsCart));
          }
          else {
            localStorage.setItem('localcart', JSON.stringify(this.itemsCart));
          }

        }
        this.cartNumberFunc();
  }

  cartNumber:Number = 0;
  cartNumberFunc() {
    var cartValue = JSON.parse(localStorage.getItem('localcart') || '{}');
    this.cartNumber = cartValue.length;
    this.user.cartSubject.next(this.cartNumber);
    console.log(this.cartNumber);
  }

  
  
  Products(items:any): any[] {
    if (this.categoryFilter !== 'all') { 
    return items.filter((u:any) => u.category === this.categoryFilter);
    }
    else {
      return items;
    }  
  }

  
  // For Quantity Increment & Decrement 
  
  inc(product:any) {
    // console.log(product.quantity);
    if(product.quantity!= 5){
    product.quantity += 1;
    }
  }

  dec(product:any) {
    // console.log(product.quantity);
    if(product.quantity!= 1){
      product.quantity -= 1;
      }
  }


